<?$SQLArray = $_POST['SQLArray'];try {	$MYOBDatabase = new PDO("odbc:Driver={MYOAU1001}; Database=MYOBDevelopment; ");	$MYOBDatabase->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);	$MYOBDatabase->beginTransaction();	echo "Connected to MYOB successfully.\n";} catch (Exception $e) {	die("Unable to connect to MYOB: " . $e->getMessage());}foreach ($SQLArray as $SQLString) {	$MYOBDatabase->exec($SQLString);}try {	$MYOBDatabase->commit();	echo "Invoice successfully submitted.\n";} catch (Exception $e) {	echo "Import failed: ". $e->getMessage(); }?>